        public static bool isNumeric(string str) //判断是否为有效数字
        {
            bool i;
            if (str != null && System.Text.RegularExpressions.Regex.IsMatch(str, @"^-?\d+$"))
                i = true;
            else
                i = false;
            return i;
        }

        public static double CalSu(String expression) //后缀表达式计算
        {
            string[] units = expression.Trim().Split(' ');
            Stack arguments = new Stack();

            foreach (string unit in units)
            {
                if (isNumeric(unit))
                {
                    arguments.Push(double.Parse(unit));
                }
                else
                {
                    double value = (double)arguments.Pop();

                    switch (unit)
                    {
                        case "+":
                            arguments.Push((double)arguments.Pop() + value);
                            break;

                        case "-":
                            arguments.Push((double)arguments.Pop() - value);
                            break;

                        case "*":
                            arguments.Push((double)arguments.Pop() * value);
                            break;

                        case "/":
                            arguments.Push((double)arguments.Pop() / value);
                            break;

                        case "sin":
                            arguments.Push(Math.Sin(value));
                            break;

                        case "cos":
                            arguments.Push(Math.Cos(value));
                            break;

                        case "ln":
                            arguments.Push(Math.Log(value));
                            break;

                        case "exp":
                            arguments.Push(Math.Exp(value));
                            break;

                        case "sqrt":
                            arguments.Push(Math.Sqrt(value));
                            break;

                        default:
                            throw new ArgumentException("未知的符号" + unit);
                    }
                }
            }

            if (arguments.Count != 1)
            {
                throw new ArgumentException("表达式不合法");
            }

            return (double)arguments.Pop();

        }
        

        string str1;
        public Boolean Priority(String ch1, String ch2)
        {
            int code1=0, code2=0;
            switch (ch1)
            {
                case "(":
                    code1 = 1;
                    break;
                case "+":
                case "-":
                    code1 = 2;
                    break;
                case "*":
                case "/":
                    code1 = 3;
                    break;
            }
            switch (ch2)
            {
                case "(":
                    code2 = 1;
                    break;
                case "+":
                case "-":
                    code2 = 2;
                    break;
                case "*":
                case "/":
                    code2 = 3;
                    break;
            }
            if (code1 > code2)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
         //中缀转后缀方法
        public void Change(string str)
        {
            string[] sz = str.Split(' ');
            Stack stack1 = new Stack();
            Stack stack2 = new Stack();


            for (int i = 0; i <= sz.Length - 1; ++i)
            {
                if (string.IsNullOrEmpty(sz[i]))                        //分隔后为空的元素剔除
                    continue;
                else if (isNumeric(sz[i]))                     //纯数字直接入队列
                    str1 += sz[i] + ' ';                
                else
                {
                    switch (sz[i])
                    {
                        case "+":     //加减乘除，按照优先级来操作。
                            if (isEmpty(stack2))
                            {
                                stack2.Push(sz[i]);
                            } else {
                                String ch = (String)stack2.Peek();
                                if (ch != "(")
                                {
                                    if (Priority(sz[i], ch))
                                    {
                                        stack2.Push(sz[i]);
                                    }
                                    else
                                    {
                                        while (!Priority(sz[i], ch))
                                        {
                                            str1 += stack2.Pop();
                                            str1 += ' ';
                                            if (isEmpty(stack2))
                                            {
                                                ch = null;
                                            }
                                            else
                                            {
                                                ch = (String)stack2.Peek();
                                            }
                                            
                                            if (ch == "(" || ch == null)
                                                break;
                                        }
                                        stack2.Push(sz[i]);
                                    }
                                }
                                else
                                {
                                    stack2.Push(sz[i]);
                                }
                            }
                            break;
                        case "-":
                            if (isEmpty(stack2))
                            {
                                stack2.Push(sz[i]);
                            }
                            else
                            {
                                String ch = (String)stack2.Peek();
                                if (ch != "(")
                                {
                                    if (Priority(sz[i], ch))
                                    {
                                        stack2.Push(sz[i]);
                                    }
                                    else
                                    {
                                        while (!Priority(sz[i], ch))
                                        {
                                            str1 += stack2.Pop(); str1 += ' ';
                                            if (isEmpty(stack2))
                                            {
                                                ch = null;
                                            }
                                            else
                                            {
                                                ch = (String)stack2.Peek();
                                            }
                                            if (ch == "(" || ch == null)
                                                break;
                                        }
                                        stack2.Push(sz[i]);
                                    }
                                }
                                else
                                {
                                    stack2.Push(sz[i]);
                                }
                            }
                            break;
                        case "*":
                            if (isEmpty(stack2))
                            {
                                stack2.Push(sz[i]);
                            }
                            else
                            {
                                String ch = (String)stack2.Peek();
                                if (ch != "(")
                                {
                                    if (Priority(sz[i], ch))
                                    {
                                        stack2.Push(sz[i]);
                                    }
                                    else
                                    {
                                        while (!Priority(sz[i], ch))
                                        {
                                            str1 += stack2.Pop(); str1 += ' ';
                                            if (isEmpty(stack2))
                                            {
                                                ch = null;
                                            }
                                            else
                                            {
                                                ch = (String)stack2.Peek();
                                            }
                                            if (ch == "(" || ch == null)
                                                break;
                                        }
                                        stack2.Push(sz[i]);
                                    }
                                }
                                else
                                {
                                    stack2.Push(sz[i]);
                                }
                            }
                            break;
                        case "/":
                            if (isEmpty(stack2))
                            {
                                stack2.Push(sz[i]);
                            }
                            else
                            {
                                String ch = (String)stack2.Peek();
                                if (ch != "(")
                                {
                                    if (Priority(sz[i], ch))
                                    {
                                        stack2.Push(sz[i]);
                                    }
                                    else
                                    {
                                        while (!Priority(sz[i], ch))
                                        {
                                            str1 += stack2.Pop(); str1 += ' ';
                                            if (isEmpty(stack2))
                                            {
                                                ch = null;
                                            }
                                            else
                                            {
                                                ch = (String)stack2.Peek();
                                            }
                                            if (ch == "(" || ch == null)
                                                break;
                                        }
                                        stack2.Push(sz[i]);
                                    }
                                }
                                else
                                {
                                    stack2.Push(sz[i]);
                                }
                            }
                            break;
                        case "(":              //左括号，直接入栈
                            stack2.Push(sz[i]);
                            break;
                        case ")":             //右括号，出栈直到遇见 ( ,跳出循环后把左括号出栈。
                            if (!isEmpty(stack2))
                            {
                                String ch = (String)stack2.Peek();
                                while (ch != "(")
                                {
                                    str1 += stack2.Pop(); str1 += ' ';
                                    ch = (String)stack2.Peek();
                                }
                                stack2.Pop();
                            }
                            break;
                    }
                }

            }
            while (!isEmpty(stack2))    //清空栈，把剩余的符号拼上去。
            {
                str1 += stack2.Pop(); str1 += ' ';
            }

        }
        public bool isEmpty(Stack st)
        {
            if (st.Count ==0)
            {
                return true;
            }
            else
            {
                return false;
            }
        }
        

